  background-color: var(--background);
  color: var(--on-background);
}
.container {
  min-height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  max-width: 1200px;
  width: 100%;
  margin: 0 auto;
  padding: 0 10px;
}

.nav__toggler {
  width: 30px;
  height: 30px;
  display: none;
  position: relative;
  cursor: pointer;
}

.nav__toggler::before {
  content: '';
  display: block;
  width: 30px;
  height: 5px;
  background-color: var(--primary-color);
  position: absolute;
  top: 10px;
  left: 0;
}
.nav__toggler::after {
  content: '';
  display: block;
  width: 30px;
  height: 5px;
  background-color: var(--primary-color);
  position: absolute;
  top: 25px;
  left: 0;
}

.nav {
  padding: 15px 30px;
  left: 0vh;
  position: fixed;
  display: flex;
  justify-content: space-between;
  align-items: center;
  z-index: 100;
  width: 100vw;
  font-family: Oswald, sans-serif;
  background: var(--on-primary);
  box-shadow: 1px 5px 15px rgba(0, 0, 0, 0.5);
}
.nav__link {
  text-decoration: none;
  color: var(--primary-variant);
  margin-right: 10px;
  font-weight: 900;
  text-transform: uppercase;
  font-size: 30px;
}
.nav__link:hover {
  color: var(--on-background);
}
.btn {
  padding: 12px;
  text-decoration: none;
  border-radius: 20px;
  background-color: var(--primary-variant);
  color: #fff;
  text-transform: uppercase;
  font-weight: 900;
  font-family: Oswald, sans-serif;
  margin-top: 50px;
  display: block;
  width: 140px;
  text-align: center;
  box-shadow: 1px 5px 5px rgba(0, 0, 0, 0.5);
  transition: all 0.3s ease-in-out;
}
.btn:active {
  box-shadow: 0 0 0;
}
.tech-tools {
  color: var(--primary-variant);
}
.main-offer__tilte {
  font-size: 50px;
  width: 320px;
  font-family: sans-serif;
}
.main-offer__tilte_name {
  font-size: 50px;
  color: var(--primary-variant);
}
.changed-content__wrapper {
  overflow: hidden;
  display: inline-block;
  width: 180px;
  padding: 0 5px;
  height: 36px;
  margin-bottom: -4px;
  background-color: var(--primary-color);
  text-align: center;
}
.changed-content__title {
  font-weight: 700;
  font-style: italic;
  text-align: center;
}
.arrow-go-home {
  position: fixed;
  bottom: 10vh;
  right: 10vh;
  width: 60px;
  height: 60px;
  border-radius: 50%;
  background-color: var(--primary-variant);
  display: flex;
  justify-content: center;
  align-items: center;
  color: #fff;
  text-decoration: none;
  border: 3px solid rgba(255, 255, 255, 0.5);
  opacity: 0;
  visibility: hidden;
  transition: all 0.5s ease-in-out;
  font-family: Oswald, sans-serif;
}
.arrow-go-home.show {
  opacity: 1;
  visibility: visible;
}

/* Media Queries */
/*Mobile devices*/

/* Contact Section */
.social-icons {
  padding: 50px;
  display: flex;
  justify-content: space-around;
  width: 100%;
}
.social-icons__link {
  font-size: 50px;
  cursor: pointer;
  color: var(--primary-color);
}
.mail-link {
  font-size: 28px;
  text-decoration: none;
  color: var(--primary-color);
}
.social-icons__link:hover,
.mail-link:hover {
  color: var(--on-background);
}

@media (max-width: 575px) {
  .container {
    max-width: 90%;
  }
}

/* phablets*/
@media (max-width: 767px) {
  .container {
    max-width: 540px;
  }
  .nav {
    height: 10vh;
  }
  .nav__menu {
    padding-top: 20vh;
    opacity: 0;
    visibility: 0;
    display: flex;
    flex-direction: column;
    align-items: center;
    position: absolute;
    top: 0px;
    left: 0;
    height: 100vh;
    width: 100vw;
    background: var(--background);
    transform: translateX(400px);
    transition: all 0.5s ease;
  }
  .nav__link {
    margin-bottom: 10px;
    transform: translateX(400px);
    transition: all 2s ease;
    opacity: 0;
  }

  @keyframes fadeIn {
    from {
      opacity: 0;
      transform: translateX(100%);
    }
    to {
      opacity: 1;
      transform: translateX(0%);
    }
  }
  .nav.active .nav__link {
    transform: translateX(0px);
  }
  .nav.active .nav__menu {
    transform: translateX('0px');
    opacity: 1;
    visibility: 1;
    transform: translateX(0px);
  }
  .nav__toggler {
    display: block;
  }
  .nav.active .nav__toggler::after {
    transform: rotate(-45deg) translateY(-11px);
  }
  .nav.active .nav__toggler::before {
    transform: rotate(45deg) translateY(10px);
  }
}

/*tablets*/
@media (max-width: 991px) {
  .container {
    max-width: 720px;
  }
}

/*laptop*/
@media (max-width: 1199px) {
  .container {
    max-width: 960px;
  }
}

/*desktop*/
@media (max-width: 1200px) {
  .container {
    max-width: 1140px;
  }
}

